INT16 CDlpFile_OnPathChanged(CDlpObject* __this)
{
	GET_THIS_VIRTUAL_RV(CDlpFile,NOT_EXEC);
	{
    /* Remove tailing slashes/backslashes */
    char* tx = &_this->m_lpsPath[dlp_strlen(_this->m_lpsPath)-1];
    while (*tx == '\\' || *tx == '/')
    {
      *tx = 0;
      if (tx == _this->m_lpsPath) break;
      tx--;
    }
	}

	return O_K;
}

